def factorial(n: int) -> int:
    if n == 0:
        return 1
    else:
        return n * factorial(n - 1)

def factorial(n: int) -> int:
    # base case: factorial of 0 or 1 is 1
    if n == 0 or n == 1:
        return 1

    # recursive case: multiply n with factorial of (n-1)
    return n * factorial(n - 1)
